import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';

const drawerWidth = 240;

const useStyles = theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
        }),
    },
    layout: {
        width: 'auto',
        marginTop: theme.spacing(3),
        marginLeft: 100,
        marginRight: theme.spacing(3),
    },
    paper: {
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(3),
        padding: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
            marginTop: theme.spacing(6),
            marginBottom: theme.spacing(6),
            padding: theme.spacing(3),
        },
    },
    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
});

class mucom extends React.Component {
    constructor(props){
        super(props);
        console.log(this.props.match.params.id);
    }

    render() {
        const { classes } = this.props;
        return (
            <div className={classes.root}>
                <AppBar position="fixed">
                    <Toolbar>
                        <Typography variant="h6" noWrap>
                            Episkey Technologies
                        </Typography>
                    </Toolbar>
                </AppBar>
                <main className={classes.layout}>
                    <Paper className={classes.paper}>
                        <React.Fragment>
                        <Grid container spacing={3}>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="company"
                                    name="Company"
                                    label="Company"
                                    fullWidth
                                    autoComplete="company-name"
                                    value={this.state.name|| ''}
                                    onChange={(e) =>{this.handleChangeInput('name',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="cname"
                                    name="cname"
                                    label="Primary Contact"
                                    fullWidth
                                    autoComplete="given-name"
                                    value={this.state.contact || ''}
                                    onChange={(e) =>{this.handleChangeInput('contact',e)}}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="email"
                                    name="email"
                                    label="Primary Email"
                                    fullWidth
                                    autoComplete="email"
                                    value={this.state.email || ''}
                                    onChange={(e) =>{this.handleChangeInput('email',e)}}
                                />
                                
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="phone"
                                    name="phone"
                                    label="Primary Phone"
                                    fullWidth
                                    autoComplete="phone"
                                    value={this.state.phone || ''}
                                    onChange={(e) =>{this.handleChangeInput('phone',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="address1"
                                    name="address1"
                                    label="Address line 1"
                                    fullWidth
                                    autoComplete="shipping address-line1"
                                    value={this.state.addr1 || ''}
                                    onChange={(e) =>{this.handleChangeInput('addr1',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    id="address2"
                                    name="address2"
                                    label="Address line 2"
                                    fullWidth
                                    autoComplete="shipping address-line2"
                                    value={this.state.addr2 || ''}
                                    onChange={(e) =>{this.handleChangeInput('addr2',e)}}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="city"
                                    name="city"
                                    label="City"
                                    fullWidth
                                    autoComplete="shipping address-level2"
                                    value={this.state.city || ''}
                                    onChange={(e) =>{this.handleChangeInput('city',e)}}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField id="state" name="state" label="State/Province/Region" fullWidth 
                                    value={this.state.state || ''}
                                    onChange={(e) =>{this.handleChangeInput('state',e)}}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="zip"
                                    name="zip"
                                    label="Zip / Postal code"
                                    fullWidth
                                    autoComplete="shipping postal-code"
                                    value={this.state.zipcode || ''}
                                    onChange={(e) =>{this.handleChangeInput('zipcode',e)}}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="country"
                                    name="country"
                                    label="Country"
                                    fullWidth
                                    autoComplete="shipping country"
                                    value={this.state.country || ''}
                                    onChange={(e) =>{this.handleChangeInput('country',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    id="instruct"
                                    name="instruct"
                                    label="Instructions"
                                    fullWidth
                                    autoComplete="instruct"
                                    value={this.state.instruct || ''}
                                    onChange={(e) =>{this.handleChangeInput('instruct',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}
                                    container
                                    direction="row"
                                    justify="center"
                                    alignItems="center">
                                    <Button variant="contained"  color="primary" onClick={() => this.saveCustomer()}>Save</Button>
                                </Grid>
                            </Grid>
                        </React.Fragment>
                    </Paper>
                </main>
            </div>
        )
    }
}

export default withStyles(useStyles)(mucom);
