import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const drawerWidth = 240;

const useStyles = theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
        }),
    },
    layout: {
        width: 'auto',
        marginTop: theme.spacing(3),
        marginLeft: 100,
        marginRight: theme.spacing(3),
    },
    paper: {
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(3),
        padding: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
            marginTop: theme.spacing(6),
            marginBottom: theme.spacing(6),
            padding: theme.spacing(3),
        },
    },
    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
});

class mucom extends React.Component {
    constructor(props){
        super(props);
        console.log(this.props.match.params.id);
    }

    render() {
        const { classes } = this.props;
        return (
            <div className={classes.root}>
                <AppBar position="fixed">
                    <Toolbar>
                        <Typography variant="h6" noWrap>
                            Episkey Technologies
                        </Typography>
                    </Toolbar>
                </AppBar>
                <main className={classes.layout}>
                    <Paper className={classes.paper}>
                        <React.Fragment>
                        <Grid container spacing={3}>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="Name"
                                    name="Name"
                                    label="Name"
                                    fullWidth
                                    onChange={(e) =>{this.handleChangeInput('name',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="email"
                                    name="email"
                                    label="Primary Email"
                                    fullWidth
                                    autoComplete="email"
                                    onChange={(e) =>{this.handleChangeInput('email',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    id="issue"
                                    name="issue"
                                    label="Instructions"
                                    fullWidth
                                    autoComplete="instruct"
                                    value={this.state.instruct || ''}
                                    onChange={(e) =>{this.handleChangeInput('instruct',e)}}
                                />
                                </Grid>
                                <Grid item xs={12}
                                    container
                                    direction="row"
                                    justify="center"
                                    alignItems="center">
                                    <Button variant="contained"  color="primary" onClick={() => this.saveCustomer()}>Save</Button>
                                </Grid>
                            </Grid>
                        </React.Fragment>
                    </Paper>
                </main>
            </div>
        )
    }
}

export default withStyles(useStyles)(mucom);
