import { ADD_ORGID } from "../actiontypes";

const initialState = {
    ByOrgId: {}
};

export default function(state = initialState, action) {
    switch (action.type) {
    case ADD_ORGID: {
        const { id, content } = action.payload;
        return {
            ...state,
            
            byIds: {
                ...state.byIds,
                [id]: {
                content,
                completed: false
                }
            }
        };
    }
    default:
        return state;
    }
}
