import { ADD_ORGID } from "../constants/actiontypes";

const initialState = {
    orgstate: {}
};

export default function(state = initialState, action) {
    switch (action.type) {
    case ADD_ORGID: {
        const { id, att } = action.payload;
        return {
            ...state,
            byIds: {
                ...state.byIds,
                [id]: { att }
                }
          };
    }
    default:
        return state;
    }
}
