import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import { connect } from 'react-redux';
import { OrgRecAction } from '../statemt/actions';
import api from '../api';

const useStyles = theme => ({
    layout: {
        width: 'auto',
        marginTop: theme.spacing(2),
        marginLeft: theme.spacing(2),
        marginRight: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
            width: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
        },
    },
    paper: {
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(3),
        padding: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
            marginTop: theme.spacing(6),
            marginBottom: theme.spacing(6),
            padding: theme.spacing(3),
        },
    },
    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
});

function mapStateToProps(state) {
    console.log(state);
    const { orgStates } = state;
    const { id, att } = orgStates;
    return{
        orgid:id,
        att:att,
    }
}

const mapDispatchToProps = dispatch => ({
    OrgRecAction: (id,payload) => dispatch(OrgRecAction(id,payload))
});

class insertCust extends React.Component{
    
    constructor(props){
        super(props);
        const { match: { params } } = this.props;
        //console.log('Insert Cust:' + props.orgid);
                
        this.state = {
            name: '',
            cname:'',
            email:'',
            status:0,
        }
    }

    saveCustomer = (saveid) =>{
        console.log("Event" + saveid);
        if(this.props.att!=saveid){
            this.props.OrgRecAction(this.props.orgid,saveid);
        }
    }

    getSteps = () =>{
        return ['New', 'Pending', 'Active'];
    }

    render(props) {
        const { classes } = this.props;
        const steps = this.getSteps();

        return (
            <React.Fragment>
                <main className={classes.layout}>
                    <div className={classes.toolbar} />
                    <Paper className={classes.paper}>
                        <Stepper activeStep={this.state.status} alternativeLabel>
                            {steps.map((label) => (
                                <Step key={label}>
                                    <StepLabel>{label}</StepLabel>
                                </Step>
                            ))}
                        </Stepper>
                        <React.Fragment>
                            <Grid container spacing={3}>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="company"
                                    name="Company"
                                    label="Company"
                                    fullWidth
                                    autoComplete="company-name"
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="cname"
                                    name="cname"
                                    label="Contact Name"
                                    fullWidth
                                    autoComplete="given-name"
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="email"
                                    name="email"
                                    label="Contact Email"
                                    fullWidth
                                    autoComplete="email"
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    required
                                    id="address1"
                                    name="address1"
                                    label="Address line 1"
                                    fullWidth
                                    autoComplete="shipping address-line1"
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    id="address2"
                                    name="address2"
                                    label="Address line 2"
                                    fullWidth
                                    autoComplete="shipping address-line2"
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="city"
                                    name="city"
                                    label="City"
                                    fullWidth
                                    autoComplete="shipping address-level2"
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField id="state" name="state" label="State/Province/Region" fullWidth />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="zip"
                                    name="zip"
                                    label="Zip / Postal code"
                                    fullWidth
                                    autoComplete="shipping postal-code"
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    id="country"
                                    name="country"
                                    label="Country"
                                    fullWidth
                                    autoComplete="shipping country"
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextField
                                    id="instruct"
                                    name="instruct"
                                    label="Instructions"
                                    fullWidth
                                    autoComplete="instruct"
                                />
                                </Grid>
                                <Grid item xs={12}
                                    container
                                    direction="row"
                                    justify="center"
                                    alignItems="center">
                                    <Button variant="contained"  color="primary" onClick={() => this.saveCustomer("4565uoi")}>Save</Button>
                                </Grid>
                            </Grid>
                            </React.Fragment>
                            
                    </Paper>
                </main>
            </React.Fragment>
        );
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(withStyles(useStyles)(insertCust));
