import { ADD_ORGID } from "../constants/actiontypes";

const initialState = {
    orgstate: {}
};

export default function(state = initialState, action) {
    switch (action.type) {
    case ADD_ORGID: {
        const { id, content } = action.payload;
        return {
            ...state,
            allIds: [...state.allIds, id],
            byIds: {
              ...state.byIds,
              [id]: {
                content,
                completed: false
              }
            }
          };
    }
    default:
        return state;
    }
}
