import { ADD_ORGID } from "../actiontypes";

const initialState = {
    ByOrgId: {}
};
  
  export default function(state = initialState, action) {
    switch (action.type) {
      case ADD_ORGID: {
        const { id, content } = action.payload;
        return {
          ...state,
          allIds: [...state.allIds, id],
          byIds: {
            ...state.byIds,
            [id]: {
              content,
              completed: false
            }
          }
        };
      }
      case TOGGLE_TODO: {
        const { id } = action.payload;
        return {
          ...state,
          byIds: {
            ...state.byIds,
            [id]: {
              ...state.byIds[id],
              completed: !state.byIds[id].completed
            }
          }
        };
      }
      default:
        return state;
    }
  }
