import { ADD_ORGID } from "../actiontypes";

const initialState = {
    orgstate: {}
};

export default function(state = initialState, action) {
    switch (action.type) {
    case ADD_ORGID: {
        const { id, att } = action.payload;
        return {
            ...state,
            orgstate: {
                ...state.orgstate,
                [id]: { att }
            }
        };
    }
    default:
        return state;
    }
}
